mapper.update:
update sftc_order
sender_mobile=#{sender_mobile}, sender_name=#{sender_name}, sender_province=#{sender_province}, sender_city=#{sender_city}, sender_area=#{sender_area}, sender_addr=#{sender_addr}, voice=#{voice}, word_message=#{word_message}, image=#{image}, gift_card_id=#{gift_card_id}, pay_method=#{pay_method}, order_type=#{order_type}
WHERE order_number=#{order_number}

update sftc_order


        order_type=#{order_type, jdbcType=VARCHAR}


WHERE id=#{id}

update sftc_order
ship_mobile=#{ship_mobile}, ship_name=#{ship_name}, ship_province=#{ship_province}, ship_city=#{ship_city}, ship_area=#{ship_area}, ship_addr=#{ship_addr}, type=#{type}, size=#{size}, gmt_pay_create=#{gmt_pay_create}, state=#{state}, gmt_pay_create=#{gmt_pay_create}, state=#{state}, courier_id=#{courier_id},
WHERE order_number=#{order_number}

update sftc_order
set region_type=#{region_type, jdbcType=VARCHAR}
WHERE id=#{id}

mapper.select:
SELECT

FROM sftc_order o,sftc_order_express oe,sftc_gift_card gc WHERE o.id=#{id}
and oe.order_id=o.id AND o.gift_card_id=gc.id

SELECT * FROM sftc_order o
WHERE id IN (
    SELECT t.order_id FROM (
      SELECT distinct oe.order_id FROM sftc_order_express oe WHERE oe.sender_user_id=#{id}
    ) AS t
)
ORDER by o.create_time desc
limit #{pageNum}, #{pageSize}

SELECT * FROM sftc_order o
WHERE id IN (
    SELECT t.order_id FROM (
      SELECT distinct oe.order_id FROM sftc_order_express oe WHERE oe.sender_user_id=#{id} OR oe.ship_user_id=#{id}
    ) AS t
)
ORDER by o.create_time desc
limit #{pageNum}, #{pageSize}

mapper.update:
UPDATE sftc_order
set
longitude=#{longitude, jdbcType=DOUBLE},
latitude=#{latitude, jdbcType=DOUBLE}
WHERE id=#{id}
