mapper.update:
update sftc_order
sender_mobile=#{sender_mobile}, sender_name=#{sender_name}, sender_province=#{sender_province}, sender_city=#{sender_city}, sender_area=#{sender_area}, sender_addr=#{sender_addr}, voice=#{voice}, word_message=#{word_message}, image=#{image}, gift_card_id=#{gift_card_id}, pay_method=#{pay_method},
WHERE order_number=#{order_number}

mapper.select:
SELECT

FROM sftc_order o,sftc_order_express oe,sftc_gift_card gc WHERE o.id=#{id}
and oe.order_id=o.id AND o.gift_card_id=gc.id

SELECT

FROM sftc_order o,sftc_order_express oe

    oe.order_number=o.order_number AND sender_user_id=#{sender_user_id}
    and state=#{state}
