mapper.update:
update sftc_token
gmt_modified=#{gmt_modified}, gmt_expiry=#{gmt_expiry}, local_token=#{local_token}, access_token=#{access_token}, refresh_token=#{refresh_token},
WHERE user_id=#{user_id}

mapper.select:
SELECT t.access_token
FROM sftc_user u, sftc_token t
WHERE u.id = t.user_id AND u.mobile = #{mobile}

select * from sftc_token

    and id = #{id}
    and user_id LIKE '%${user_id}%'
