mapper.select:
SELECT *
FROM sftc_order o, sftc_order_express oe
WHERE o.id = oe.order_id AND oe.id = #{express_id}

mapper.update:
UPDATE sftc_order
SET region_type = #{region_type, jdbcType=VARCHAR}
WHERE id = #{id}

mapper.select:
SELECT * FROM sftc_order WHERE id IN ( SELECT o.id FROM sftc_order o, sftc_order_express oe WHERE o.id=oe.order_id AND o.sender_user_id=#{id}
AND ( o.sender_name LIKE #{keyword} OR o.sender_province LIKE #{keyword} OR o.sender_city LIKE #{keyword} OR o.sender_area LIKE #{keyword} OR o.sender_addr LIKE #{keyword} OR oe.order_number LIKE #{keyword} OR oe.ship_name LIKE #{keyword} OR oe.ship_province LIKE #{keyword} OR oe.ship_city LIKE #{keyword} OR oe.ship_area LIKE #{keyword} OR oe.ship_addr LIKE #{keyword} OR oe.state LIKE #{keyword} )
) ORDER by create_time desc limit #{pageNum}, #{pageSize}

SELECT * FROM sftc_order WHERE id IN ( SELECT o.id FROM sftc_order o, sftc_order_express oe WHERE o.id=oe.order_id AND o.sender_user_id=#{id}
AND (oe.state REGEXP ${keyword_state} )
AND ( o.sender_name LIKE #{keyword} OR o.sender_province LIKE #{keyword} OR o.sender_city LIKE #{keyword} OR o.sender_area LIKE #{keyword} OR o.sender_addr LIKE #{keyword} OR oe.order_number LIKE #{keyword} OR oe.ship_name LIKE #{keyword} OR oe.ship_province LIKE #{keyword} OR oe.ship_city LIKE #{keyword} OR oe.ship_area LIKE #{keyword} OR oe.ship_addr LIKE #{keyword} )
) ORDER by create_time desc limit #{pageNum}, #{pageSize}

mapper.update:
UPDATE sftc_order
SET is_cancel = 1
WHERE id = #{id}

mapper.select:
SELECT * FROM sftc_order

    and id = #{id}
    and order_type = #{order_type}
    and region_type = #{region_type}
    and sender_mobile = #{sender_mobile}
